 Log.d("Header", "Favorite clicked - $wishlistCount items")
 },
 onCartClick = {
 Log.d("Header", "Cart clicked - $cartCount items")
 }
 )
 }

 // Search Filter Widget
 item {
 AnimatedVisibility(
 visible = showSearchFilter,
